/* 确保导航栏图标对齐 */
#leftNavDrawerIcon, 
#sys-settings-button .drawer-icon, 
#advanced-formatting-button .drawer-icon, 
#WI-SP-button .drawer-icon, 
#user-settings-button .drawer-icon, 
#logo_block .drawer-icon, 
#extensions-settings-button .drawer-icon, 
#persona-management-button .drawer-icon, 
#rightNavDrawerIcon {
    position: relative;
    display: flex;
    align-items: center;
}

/* 统一所有图标的公共样式 */
#leftNavDrawerIcon::before,
#sys-settings-button .drawer-icon::before,
#advanced-formatting-button .drawer-icon::before,
#WI-SP-button .drawer-icon::before,
#user-settings-button .drawer-icon::before,
#logo_block .drawer-icon::before,
#extensions-settings-button .drawer-icon::before,
#persona-management-button .drawer-icon::before,
#rightNavDrawerIcon::before {
    content: "";
    position: absolute;
    background-size: contain;
    background-repeat: no-repeat;
    opacity: 1;
    filter: saturate(1.4);
}

/* 单独调整每个图标的位置和大小 */
#leftNavDrawerIcon::before {
    background-image: url('https://files.catbox.moe/69pnam.png');
    width: 60px; /* 调整宽度 */
    height: 36px; /* 调整高度 */
    left: 4px; /* 调整水平位置 */
    top: 55%; /* 调整垂直位置 */
    transform: translateY(-50%);
}

#sys-settings-button .drawer-icon::before {
    background-image: url('https://files.catbox.moe/mt5xu9.png');
